import 'package:bloc/bloc.dart';import 'package:flutter_bloc/flutter_bloc.dart';import 'package:newsapp/manger/state.dart';import 'package:newsapp/model/artical.dart';import 'package:newsapp/network/api_service.dart';class AppCubit extends Cubit <AppState> {  ApiService apiService = ApiService();  AppCubit():super(InitialState());  static AppCubit get(context) => BlocProvider.of(context);  getNws({String category= "general"})async{    emit(LoadingState()); // دي االلي بتديني state   try {     var json = await apiService.get(endpoint: 'top-headlines?country=us&category=$category&apiKey=6b0c10e58ff54f4caa1e9f252329dc30');     List<ArticalModel> articles = [];     for (var item in json) {       articles.add(ArticalModel(title: item.title, description: item.description, urlToImage: item.urlToImage));     }     emit(LoadedState(x: articles));   }  catch (error) {     emit(ErrorState(errorMessage: error.toString()));     // TODO   }  }//  BlocProvider , BlocBuilder, BlocListener}