import 'package:dio/dio.dart';import 'package:newsapp/model/artical.dart';class ApiService {  final baseUrl = ("https://newsapi.org/v2/");  final Dio dio = Dio();  Future<List<ArticalModel>> get({required String endpoint}) async {    var response = await dio.get(        "$baseUrl$endpoint");    Map<String, dynamic> json = response.data;    List<ArticalModel> articles = [];    for (var item in json["articles"]) {    articles.add(ArticalModel(title: item["title"], description: item["description"],urlToImage: item["urlToImage"]));    }    return articles;  }  }