import 'package:flutter/material.dart';import 'package:newsapp/model/artical.dart';late final ArticalModel articalModel;class ArticalItem extends StatefulWidget {  final dynamic articalModel;  const ArticalItem({super.key, required this.articalModel});  @override  _ArticalItemState createState() => _ArticalItemState();}class _ArticalItemState extends State<ArticalItem> {  bool isTitleExpanded = false;  bool isDescriptionExpanded = false;  @override  Widget build(BuildContext context) {    return Padding(      padding: const EdgeInsets.all(15.0),      child: Column(        crossAxisAlignment: CrossAxisAlignment.start,        children: [          Container(            width: double.infinity,            height: 210,            decoration: BoxDecoration(              borderRadius: BorderRadius.circular(40),              image:  DecorationImage(                fit: BoxFit.fill,                image: NetworkImage(                  articalModel.urlToImage,                ),              ),            ),          ),          const SizedBox(height: 5),          GestureDetector(            onTap: () {              setState(() {                isTitleExpanded = !isTitleExpanded;              });            },            child: Text(             articalModel.title,              maxLines: isTitleExpanded ? null : 2,              overflow: isTitleExpanded ? TextOverflow.visible : TextOverflow.ellipsis,              style: const TextStyle(color: Colors.black, fontSize: 20),            ),          ),          GestureDetector(            onTap: () {              setState(() {                isDescriptionExpanded = !isDescriptionExpanded;              });            },            child: Text(              articalModel.description,              maxLines: isDescriptionExpanded ? null : 2,              overflow: isDescriptionExpanded ? TextOverflow.visible : TextOverflow.ellipsis,              style: const TextStyle(color: Colors.grey),            ),          ),        ],      ),    );  }}